import React, { useState } from "react";
import ReactDOM from "react-dom";
import DragItem from "./components/drag-item";
import DropItem from "./components/drop-item";

import "./styles.css";

const items = {
  1: {
    text: "First box",
    state: "todo"
  },
  2: {
    text: "Second box",
    state: "todo"
  },
  3: {
    text: "Third box",
    state: "todo"
  },
  4: {
    text: "Fourth box",
    state: "todo"
  }
};

function App() {
  const [todoValues, setValue] = useState(items);
  return (
    <div className="App">
      <div className="box">
        <DropItem
          heading="Drag And Drop Items"
          onDrop={id => {
            const currentTodo = { ...todoValues[id] };
            currentTodo.state = "todo";
            setValue({ ...todoValues, ...{ [id]: currentTodo } });
          }}
        >
          {Object.keys(todoValues)
            .map(key => ({ id: key, ...todoValues[key] }))
            .filter(todo => todo.state === "todo")
            .map(todo => <DragItem id={todo.id} data={todo} key={todo.id} />)}
        </DropItem>

        <DropItem
          heading="Done"
          onDrop={id => {
            const currentTodo = { ...todoValues[id] };
            currentTodo.state = "done";
            setValue({ ...todoValues, ...{ [id]: currentTodo } });
          }}
        >
          {Object.keys(todoValues)
            .map(key => ({ id: key, ...todoValues[key] }))
            .filter(todo => todo.state === "done")
            .map(todo => <DragItem id={todo.id} data={todo} key={todo.id} />)}
        </DropItem>
      </div>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
